package KinomotoSakuraMod.Cards.ClowCard;import KinomotoSakuraMod.Actions.KSMOD_MoveAction;import KinomotoSakuraMod.Cards.KSMOD_AbstractMagicCard;import KinomotoSakuraMod.Patches.KSMOD_CustomCardColor;import KinomotoSakuraMod.Patches.KSMOD_CustomTag;import com.megacrit.cardcrawl.characters.AbstractPlayer;import com.megacrit.cardcrawl.core.CardCrawlGame;import com.megacrit.cardcrawl.dungeons.AbstractDungeon;import com.megacrit.cardcrawl.localization.CardStrings;import com.megacrit.cardcrawl.monsters.AbstractMonster;public class ClowCardTheMove extends KSMOD_AbstractMagicCard{    public static final String ID = "ClowCardTheMove";    private static final String NAME;    private static final String DESCRIPTION;    private static final String[] EXTENDED_DESCRIPTION;    private static final String IMAGE_PATH = "img/cards/clowcard/the_move.png";    private static final int COST = 1;    private static final int UPGRADED_COST = 0;    private static final CardType CARD_TYPE = CardType.SKILL;    private static final CardColor CARD_COLOR = KSMOD_CustomCardColor.CLOWCARD_COLOR;    private static final CardRarity CARD_RARITY = CardRarity.UNCOMMON;    private static final CardTarget CARD_TARGET = CardTarget.NONE;    static    {        CardStrings cardStrings = CardCrawlGame.languagePack.getCardStrings(ID);        NAME = cardStrings.NAME;        DESCRIPTION = cardStrings.DESCRIPTION;        EXTENDED_DESCRIPTION = cardStrings.EXTENDED_DESCRIPTION;    }    public ClowCardTheMove()    {        super(ID, NAME, IMAGE_PATH, COST, DESCRIPTION, CARD_TYPE, CARD_COLOR, CARD_RARITY, CARD_TARGET, true);        this.tags.add(KSMOD_CustomTag.KSMOD_FIREY_CARD);        this.exhaust = true;    }    @Override    public void upgrade()    {        if (!this.upgraded)        {            upgradeName();            this.upgradeBaseCost(UPGRADED_COST);        }    }    @Override    public KSMOD_AbstractMagicCard makeCopy()    {        return new ClowCardTheMove();    }    @Override    public void applyNormalEffect(AbstractPlayer player, AbstractMonster monster)    {        AbstractDungeon.actionManager.addToBottom(new KSMOD_MoveAction(1, false));    }    @Override    public void applyExtraEffect(AbstractPlayer player, AbstractMonster monster)    {        AbstractDungeon.actionManager.addToBottom(new KSMOD_MoveAction(1, true));    }    @Override    public String getExtraDescription()    {        return EXTENDED_DESCRIPTION[0];    }}